function longestCommonPrefix(arr) {
    if(arr.length == 0) {
        return "";
    }

    let prefix = arr[0];

    for(let i = 1; i < arr.length; i++) {
        let j = 0;
        while (j < arr[i].length && j < prefix.length && 
               arr[i][j] === prefix[j]) {
            j++;
        }
        prefix = prefix.substring(0, j);
    }
    return prefix;
}

