int isPalindrome(int num)
{ 
    int orig_num, rest, sum= 0;

    orig_num = num; 

    while (num > 0)
    {
        rest = num % 10; 
        sum = (sum * 10) + rest; 
        num = num / 10; 
    } 

    if (orig_num == sum) 
        return 1; 

    return 0;
} 

int main (void)
{
    int num = 1221;

    if (isPalindrome(num))
