def longest_palindrome(string):
    s_len = len(string)
    l_palindrome = ""

    i = 0
    while i < s_len:
        j = s_len
        while j > i:
            if string[i:j] == string[i:j][::-1]:
                if len(string[i:j]) > len(l_palindrome):
                    l_palindrome = string[i:j]
            j -= 1
        i += 1

    return l_palindrome

