    n1 = len(str1)
    n2 = len(str2)
    result = ""
    len_result = 0
    mat = [[0 for x in range(n2)] for y in range(n1)]

    for i in range(n1):
        for j in range(n2):
            if str1[i] == str2[j]:
                if i == 0 or j == 0:
                    mat[i][j] = 1

                else:
                    mat[i][j] = mat[i-1][j-1]+1

