public static float triangleArea(float a, float b, float c) {  
    //calculate the semi-perimeter  
    float s = (a + b + c) / 2;  

    //calculate the area  
    float area = (float)Math.sqrt(s * (s - a) * (s - b) * (s - c));  
    return area;
}

