def insertNodeAtIndex(head, value, index):
    if index == 0:
        new_node = Node(value)
        new_node.next = head
        return new_node
    curr = head
    for i in range(index - 1):
        curr = curr.next
    if curr is None:
        return head
    else:
        new_node = Node(value)
        new_node.next = curr.next
        curr.next = new_node
    return head

