function binarySearch(array, x) {
  let low = 0;
  let high = array.length - 1;

  while (low <= high) {
    let mid = Math.floor((low + high) / 2);
    let item = array[mid]; 

    if (x === item) {
      return mid;
    } 

    if (x < item) {
      high = mid - 1;
    } else {
      low = mid + 1;
    }
  }
}

