public class RemoveDuplicates 
{ 
    public static int removeDuplicates(int arr[], int n) 
    { 
        if (n == 0 || n == 1) 
            return n; 

        int[] temp = new int[n]; 

        int j = 0; 
        for (int i = 0; i < n-1; i++) 
            if (arr[i] != arr[i+1]) 
                temp[j++] = arr[i]; 

        temp[j++] = arr[n-1];    

        for (int i=0; i<j; i++) 
            arr[i] = temp[i]; 

        return j; 
    } 

    public static void main (String[] args) 
    { 
        int arr[] = {1,1,2,3,3,4,4,5,6}; 
        int n = arr.length; 

        n = removeDuplicates(arr, n); 

