    curr = head 
    if curr is None or node_to_delete is None: 
	    return
    if curr == node_to_delete: 
	    head = curr.next 
	    node_to_delete = None
	    return
    while curr.next is not None: 
        if curr.next == node_to_delete: 
            curr.next = node_to_delete.next 
            node_to_delete = None
            return
        else:  
            curr = curr.next

