def longest_common_sequence(string1, string2):
        s1 = len(string1)
        s2 = len(string2)
        lcs =  [[0 for x in range(s2+1)] for x in range(s1+1)]

        for i in range(s1):
            for j in range(s2):

                if string1[i] == string2[j]:
                    lcs[i+1][j+1] = lcs[i][j] + 1

                else:
                    lcs[i+1][j+1] = max(lcs[i+1][j], lcs[i][j+1])

        return lcs[s1][s2]

