public static int[] deleteElement (int[] arr, int position) 
{ 
    if (position == arr.Length-1)
    {
        int[] new_arr = new int[arr.Length-1]; 
        for (int i = 0; i < arr.Length-1; i++) 
            new_arr[i] = arr[i]; 
        return new_arr;
    }
    else
    {
        int[] new_arr = new int[arr.Length-1]; 
        for (int i = 0; i < position; i++) 
            new_arr[i] = arr[i]; 

        for (int i = position+1; i < arr.Length; i++) 
            new_arr[i-1] = arr[i];

        return new_arr;
    }
}

