	public static int findSecondLargest(int[] arr) {
		int max = Integer.MIN_VALUE;
		int secondMax = Integer.MIN_VALUE;
		for (int num : arr) {
			if (num > max) {
				secondMax = max;
				max = num;			
			} else if (num > secondMax) { 
				secondMax = num;
			}	
		}	
		return secondMax;
	}

	public static void main(String[] args) {
		int[] arr = {10, 25, 15, 5};
		int result = findSecondLargest(arr);
