fn sort(arr: &mut[i32]) {
    let curr_size = arr.len();

    for i in 0..<curr_size {
        let mut j = 0;

        while j < curr_size - i - 1 {

            if arr[j] >= arr[j + 1] {
                let temp = arr[j];
                arr[j] = arr[j+1];
                arr[j+1] = temp;
            }

            j += 1;
        }
    }
}

