            //STEP 4: Execute a query 
            stmt = conn.createStatement(); 
            String query = "INSERT INTO users(name, age, occupation) VALUES(?, ?, ?)"; 
            PreparedStatement preparedStmt = conn.prepareStatement(query);

            for(int i = 0; i < name.length; i++) {
