def check_parenthesis(exp):

    stack = list()
    for character in exp:

        if character in {'(', '[', '{'}:
            stack.append(character)

        else:
            if not stack:
                return False

            top = stack.pop()
            if top is '(' and character is not ')':
                return False
            if top is '[' and character is not ']':
                return False
            if top is '{' and character is not '}':
                return False

    if stack:
        return False

    return True

