public static void rotateArray(int[] arr, int n) { 
    int temp; 
    int rotations = n % arr.length; 

    while (rotations > 0) { 
        temp = arr[arr.length - 1]; 

        for (int i = arr.length - 1; i > 0; i--) { 
            arr[i] = arr[i - 1]; 
        } 
        arr[0] = temp; 
        rotations--; 
    }  
}

