def removeItem(head, item):   
    # If head node is item
    if (head != None and head.data == item):
        head = head.next

    prev = None
    curr = head

    # Traverse the double linked list
    while (curr != None):
        # If curr node is the item
        if (curr.data == item):
            # If prev node is null
            if (prev == None):
                curr = curr.next
            else:
                prev.next = curr.next
                curr = curr.next
        else:
            prev = curr
            curr = curr.next

    return head

