public static bool IsSubstring(string s1, string s2) 
{ 
    // Iterate through both strings and check 
    // if characters of one string canbe found in 
    // another 
    int M = s1.Length; 
    int N = s2.Length; 

    for (int i = 0; i <= N - M; i++) { 
        int j; 

        // For current index i, check for pattern 
        for (j = 0; j < M; j++) 
            if (s2[i + j] != s1[j]) 
                break; 

        // if pat[0...M-1] = txt[i, i+1, ...i+M-1] 
        if (j == M) 
            return true; 
    } 

    return false;  
}

