def BFS(graph, start): 
    queue = []
    queue.append(start) 
    visited = []
    while queue: 
        node = queue.pop(0) 
        if node not in visited: 
            visited.append(node)
            for adjacent in graph.get(node, []):
                queue.append(adjacent)
    return visited

